import React, { Component } from "react";
import axios from "axios";
import { Link, Redirect } from "react-router-dom";
import moment from 'moment';

export class Posts extends Component {
  state = {

    upvote: 0,
    upvoted: false,
    posts: [],
    createRequested: false,
    createLink:"",
    createDescription:""
  };


  handleSubmit = (event) => {
    event.preventDefault();
    const url="http://localhost:8000/api/posts";
    console.log(url);

    const headers = {
      "Content-Type": "application/json",
      Accept: "application/json",
      Authorization: `JWT ${localStorage.getItem("token")}`

    };
    const link = this.state.createLink;
    const description = this.state.createDescription;
    const course=this.props.match.params.id;
    let bodyFormData = new FormData();
    bodyFormData.set("link",link);
    bodyFormData.set("description", description);
    bodyFormData.set("course", course);

    axios
      .post(url, bodyFormData, { headers })
      .then((result) => {        

        console.log(result.data);
      })
      .catch((error) => {
        console.log(error);

      });

    window.location.reload();

  }

  upvoteHandler = (e, id, val,upvoted) => {

    this.setState({upvoted:!upvoted});

    const headers = {
      "Content-Type": "application/json",
      Accept: "application/json",
      Authorization: `JWT ${localStorage.getItem("token")}`

    };
    const url = "http://localhost:8000/api/upvote";
    const user = localStorage.getItem("userid");
    const post = id;
    let bodyFormData = new FormData();
    bodyFormData.set("user", user);
    bodyFormData.set("post", post);

    console.log(user); console.log(post);
    axios
      .post(url, bodyFormData, { headers })
      .then((result) => {


        // this.setState({ posts: result.data });


        console.log(result.data);
      })
      .catch((error) => {
        console.log(error);

      });

    window.location.reload()

  }

  handleCreatepost = (event) => {
    console.log("Bye")
    this.setState({ createRequested: true });
  }

  handleCreateLinkChange = (event) => {
    this.setState({ createLink: event.target.value });
  };
  handleCreateDescriptionChange = (event) => {
    this.setState({ createDescription: event.target.value });
  };

  componentDidMount() {
    const headers = {
      "Content-Type": "application/json",
      Accept: "application/json",
      Authorization: `JWT ${localStorage.getItem("token")}`

    };
    const url = "http://localhost:8000/api/posts?course=" + this.props.match.params.id;
    axios
      .get(url, { headers })
      .then((result) => {


        this.setState({ posts: result.data });


        console.log(result.data);
      })
      .catch((error) => {
        console.log(error);

      });
  }




  render() {
    //console.log(this.props);
    return (
      <div>

        <div className="container">
          <button className="btn btn-outline-success " onClick={ this.handleCreatepost}>Create Post</button>


          {this.state.createRequested ?
            <div>
              <form onSubmit={this.handleSubmit}>
                <div className="form-group">
                  <div className="form-label-group">
                    <input
                      className={
                        "form-control "
                      }
                      id="link"
                      placeholder="Link"
                      type="text"
                      name="link"
                      onChange={this.handleCreateLinkChange}
                      autoFocus
                      required
                    />
                    <label htmlFor="link">Link</label>

                  </div>

                  <div className="form-label-group">
                    <input
                      className={
                        "form-control "
                      }
                      id="description"
                      placeholder="Description"
                      type="text"
                      name="description"
                      onChange={this.handleCreateDescriptionChange}
                      autoFocus
                      required
                    />
                    <label htmlFor="description">Description</label>

                  </div>
                </div>

                <div className="form-group">
                  <button
                    className="btn btn-primary btn-block"
                    type="submit"

                  >
                    Post &nbsp;&nbsp;&nbsp;

                  </button>
                </div>
              </form>
            </div> : <div></div>}

          <br></br>
          <br></br>
          {this.state.posts.length == 0 ? <div><h2>No Posts Yet</h2></div> : this.state.posts.map((val) => {

            return (
              <div className=" container ">
                <div className="posts">

                  <h4>Posted By:- {val.created_by.full_name} - {val.created_by.email}</h4>
                  <h6 style={{ textAlign: 'left' }}> Posted on : {moment(val.created_at).format("dddd, MMMM Do YYYY, h:mm:ss a")} </h6>
                  <h3>{val.description}</h3>

                  <a target="_blank" href={val.link}><h3> {val.link}</h3></a>


                  <h4><button className={val.is_already_upvoted ? "btn btn-success" : " btn btn-secondary"} onClick={(e) => this.upvoteHandler(e, val.id, val.upvote_count,val.is_already_upvoted)}>Upvote</button>
                    Upvote-Count {val.upvote_count}
                  </h4>



                </div>
                <br></br>
              </div>

            );
          })}
        </div>
      </div>
    );
  }
}

export default Posts;
