import React, { Component } from "react";
import axios from "axios";
import { Link, Redirect } from "react-router-dom";
import moment from 'moment';

export class Courses extends Component {
  state = {


    courses: [],
  };


  instituteHandler = (e, id,name) => {

console.log(id);
console.log(name);
// localStorage.setItem("branchSelected",id);
//     const url = "/posts/" + localStorage.getItem('branchSelected');
//     this.props.history.push(url);


  }
  componentDidMount() {
    const headers = {
        "Content-Type": "application/json",
        Accept: "application/json"

      };
      const url = "http://localhost:8000/api/courses?branch"+localStorage.getItem('branchSelected')+"&institute="+ localStorage.getItem('institute');
      axios
        .get(url, { headers })
        .then((result) => {


          this.setState({ courses: result.data });


          console.log(result.data);
        })
        .catch((error) => {
          console.log(error);

        });
  }




  render() {
    //console.log(this.props);
    return (
      <div>

        <div className="container">

          {this.state.courses.map((val) => {
            return (
              <div className=" container ">
                <div className="batches">
                  <button className="btn btn-outline-success btn-block" onClick={(e) => this.instituteHandler(e, val.id,val.name)}>
                    <h3>{val.name}</h3>

                  </button>

                </div>
                <br></br>
              </div>

            );
          })}
        </div>
      </div>
    );
  }
}

export default Courses;
