import React, { Component } from "react";
import axios from "axios";
import { Link, Redirect } from "react-router-dom";
import moment from 'moment';

export class userProfile extends Component {


    state = {
        profile: [],
        isEditing: false
    }

    editHandler = (event) => {
        this.setState({ isEditing: true });
    }
    componentDidMount() {
        const headers = {
            "Content-Type": "application/json",
            Accept: "application/json",
            Authorization: `JWT ${localStorage.getItem("token")}`

        };
        const url = "http://localhost:8000/api/user-profile";
        axios
            .get(url, { headers })
            .then((result) => {


                this.setState({ profile: result.data });


                console.log(result.data);
            })
            .catch((error) => {
                console.log(error);

            });
    }
    render() {
        return (
            <div>
                <div className="container">
                    <button className="btn btn-outline-success " onClick={this.editHandler}>Edit Details</button>
                    {!this.state.isEditing ?  

                            <div className=" container " >
                                <div className="profile">

                                    <ul>
                                    <h4 > Full Name:- {this.state.profile.full_name} </h4>
                                    <h4>Email:- {this.state.profile.email} </h4>
                                    <h4>Gender:- {this.state.profile.gender} </h4>
                                    <h4>Institute:- {this.state.profile.institute}</h4>
                                    <h4>Branch:- {this.state.profile.branch}</h4>
                                    <h4>Degree:- {this.state.profile.degree} </h4>
                                    <h4>Contact Number:- {this.state.profile.phone} </h4>
                                    </ul>
                                </div>
                                <br></br>
                            </div>


                    :<div>
                        <form onSubmit={this.handleSubmit}>
                <div className="form-group">
                  <div className="form-label-group">
                    <input
                      className={
                        "form-control "
                      }
                      id="link"
                      placeholder="Link"
                      type="text"
                      name="link"
                      onChange={this.handleCreateLinkChange}
                      autoFocus
                      required
                    />
                    <label htmlFor="link">Link</label>

                  </div>

                  <div className="form-label-group">
                    <input
                      className={
                        "form-control "
                      }
                      id="description"
                      placeholder="Description"
                      type="text"
                      name="description"
                      onChange={this.handleCreateDescriptionChange}
                      autoFocus
                      required
                    />
                    <label htmlFor="description">Description</label>

                  </div>
                </div>

                <div className="form-group">
                  <button
                    className="btn btn-primary btn-block"
                    type="submit"

                  >
                    Post &nbsp;&nbsp;&nbsp;

                  </button>
                </div>
              </form></div>}
                </div>
            </div>
        )
    }
}

export default userProfile
