import React, { Component } from "react";
import axios from "axios";
import { Link, Redirect } from "react-router-dom";
import moment from 'moment';

export class Courses extends Component {
  state = {


    courses: [],
  };


  courseHandler = (e, id,name) => {

console.log(id);
console.log(name);
localStorage.setItem("course",id);
    const url = "/posts/" + localStorage.getItem('course');
    this.props.history.push(url);


  }

  subscribeHandler=(e,id,subs)=>{
    if(!subs){
      const url="http://localhost:8000/api/email-notification-subscription";
      const headers = {
        "Content-Type": "application/json",
        Accept: "application/json",
        Authorization: `JWT ${localStorage.getItem("token")}`

      };
      const user = localStorage.getItem('userid');


    const course=id;

    let bodyFormData = new FormData();
    bodyFormData.set("course",course);
    bodyFormData.set("user", user);

    axios
    .post(url, bodyFormData, { headers })
    .then((result) => {


        // this.setState({ profile: result.data });


        console.log(result.data);
    })
    .catch((error) => {
        console.log(error);

    });

    }

    else{
      const url="http://localhost:8000/api/email-notification-subscription"+id;
      const headers = {
        "Content-Type": "application/json",
        Accept: "application/json",
        Authorization: `JWT ${localStorage.getItem("token")}`

      };

    axios
    .delete(url,  { headers })
    .then((result) => {


        // this.setState({ profile: result.data });


        console.log(result.data);
    })
    .catch((error) => {
        console.log(error);

    });

    }
  }
  componentDidMount() {
    const headers = {
        "Content-Type": "application/json",
        Accept: "application/json",
        Authorization: `JWT ${localStorage.getItem("token")}`

      };
      const url = "http://localhost:8000/api/courses?branch="+this.props.match.params.id+"&institute="+ localStorage.getItem('institute');
      axios
        .get(url, { headers })
        .then((result) => {


          this.setState({ courses: result.data });


          console.log(result.data);
        })
        .catch((error) => {
          console.log(error);

        });
  }




  render() {
    //console.log(this.props);
    return (
      <div>

        <div className="container">
          {this.state.courses.length == 0 ? <div><h1>No Courses added</h1></div> :
          this.state.courses.map((val) => {
            return (
              <div className=" container ">
                <div className="btn-group ">

                  <button style={{width: "80%"}} className="btn btn-outline-success btn-block" onClick={(e) => this.courseHandler(e, val.id,val.name)}>
                    <h3>{val.name}</h3>


                  </button>
                  <button  style={{width: "20%"}} className={val.email_notification_subscription?"btn btn-success btn-block ":"btn btn-secondary btn-block"} onClick={(e) => this.subscribeHandler(e, val.id,val.email_notification_subscription)}>
                    {val.email_notification_subscription?<h6>Bell</h6>:<h6>Hell</h6>}



                  </button>

                </div>
                <br></br>
              </div>

            );
          })
          }
        </div>
      </div>
    );
  }
}

export default Courses;
