import React, { Component } from "react";
import axios from "axios";
import { Link, Redirect } from "react-router-dom";
import moment from 'moment';

export class Posts extends Component {
  state = {

    upvote:0,
    upvoted:false,
    posts: [],
  };


  postHandler = (e, id) => {

console.log(id);


// localStorage.setItem("branchSelected",id);
//     const url = "/posts/" + localStorage.getItem('branchSelected');
//     this.props.history.push(url);


  }

  upvoteHandler=(e,val)=>{
    if(this.state.upvoted)
      {
        this.setState({upvoted:false});
        this.setState({upvote:this.state.upvote-1});
      }
    else {
      if(!val)
        {
          this.setState({upvoted:true});
        this.setState({upvote:val+1});
        }

      else {
        this.setState({upvoted:true});
        this.setState({upvote:1})
      }
    }

  }


  componentDidMount() {
    const headers = {
        "Content-Type": "application/json",
        Accept: "application/json",
        Authorization: `JWT ${localStorage.getItem("token")}`

      };
      const url = "http://localhost:8000/api/posts?course="+this.props.match.params.id;
      axios
        .get(url, { headers })
        .then((result) => {


          this.setState({ posts: result.data });


          console.log(result.data);
        })
        .catch((error) => {
          console.log(error);

        });
  }




  render() {
    //console.log(this.props);
    return (
      <div>

        <div className="container">

          {this.state.posts.map((val) => {
            return (
              <div className=" container ">
                <div className="batches">


                    <h3>{val.description}</h3>
                    <h3>{val.link}</h3>
                    <h3>Posted on : {moment(val.created_at).format("dddd, MMMM Do YYYY, h:mm:ss a")} </h3>
                    {this.setState({upvote:val.upvote_count})};
                    <button className="btn btn-outline-success btn-block" onClick={(e) => this.upvoteHandler(e, this.state.upvote)}>Upvote</button>

                    <h3>Upvote-Count {this.state.upvote}</h3>

                </div>
                <br></br>
              </div>

            );
          })}
        </div>
      </div>
    );
  }
}

export default Posts;
