import React, { Component } from "react";
import axios from "axios";
export class AllBatches extends Component {

  handleGet=(event) => {
    event.preventDefault();
    const headers = {
        "Content-Type": "application/json",
        Accept: "application/json",
        Authorization: `JWT ${localStorage.getItem("token")}`
    };
    // Typical usage (don't forget to compare props):
    console.log(this.props);
    const batch = this.props.match.params.id;
    const student = localStorage.getItem("userid");
    const batchid=parseInt(batch);
    const url="http://localhost:8000/api/batch"+this.props.location.search;
    console.log(url);
    let bodyFormData = new FormData();

    bodyFormData.set("batch", batchid);
    bodyFormData.set("student", student);
    axios
      .get(url,{ headers})
      .then((result) => {
        console.log(result.data);

      })
      .catch((error) => {
        console.log(error);

      });

  }

  handleGet=(event) => {
    event.preventDefault();
    const headers = {
        "Content-Type": "application/json",
        Accept: "application/json",
        Authorization: `JWT ${localStorage.getItem("token")}`
    };
    // Typical usage (don't forget to compare props):
    console.log(this.props);
    const batch = this.props.match.params.id;
    const student = localStorage.getItem("userid");
    const batchid=parseInt(batch);
    const url="http://localhost:8000/api/batch"+this.props.location.search;
    console.log(url);
    let bodyFormData = new FormData();

    bodyFormData.set("batch", batchid);
    bodyFormData.set("student", student);
    axios
      .get(url,{ headers})
      .then((result) => {
        console.log(result.data);

      })
      .catch((error) => {
        console.log(error);

      });

  }
  handleEmailChange = (event) => {
    this.setState({ email: event.target.value });
  };
  handlePwdChange = (event) => {
    this.setState({ password: event.target.value });
  };


  render() {
    //console.log(this.props);
    return (
      <div>
        <h1>Hello {this.props.match.params.id}</h1>
        <div className="container">
        <div className="card card-login mx-auto mt-5">
          <div className="card-header">Create Batch</div>
          <div className="card-body">
            <form onSubmit={this.handlePost}>
              <div className="form-group">
                <div className="form-label-group">
                  <input
                    type="text"
                    id="name"
                    className="form-control"
                    placeholder="Name"
                    name="name"
                    onChange={this.handleNameChange}
                    required
                  />
                  <label htmlFor="name">Name</label>
                </div>
              </div>
              <div className="form-group">
                <div className="form-label-group">
                  <input
                    type="text"
                    id="meet"
                    className="form-control"
                    placeholder="Meet Link"
                    name="meet"
                    onChange={this.meetChange}
                    required
                  />
                  <label htmlFor="meet">Meet Link</label>
                </div>
              </div>


             <div className="form-group">
                <div className="form-label-group">
                  <input
                    type="text"
                    className="form-control"
                    id="created_by"
                    placeholder="Created By"
                    name="created_by"
                    onChange={this.handleCreatedBy}
                    required
                  />
                  <label htmlFor="created_by">Created By</label>
                </div>
              </div>

              <div className="form-group">
                <button
                  className="btn btn-primary btn-block"
                  type="submit"
                  disabled={this.state.isLoading ? true : false}
                >
                  Register &nbsp;&nbsp;&nbsp;
                  {isLoading ? (
                    <span
                      className="spinner-border spinner-border-sm"
                      role="status"
                      aria-hidden="true"
                    ></span>
                  ) : (
                    <span></span>
                  )}
                </button>
              </div>
            </form>
            <div className="text-center">
              <Link className="d-block small mt-3" to={"/login"}>
                Login Your Account
              </Link>
              <Link className="d-block small" to={"#"}>
                Forgot Password?
              </Link>
            </div>
          </div>
        </div>

      </div>
      </div>
    );
  }
}

export default AllBatches;
