import React, { Component } from 'react'

export class start extends Component {

    state = {
        user: ''
      };
    handleSubmit = () => {
        const { user } = this.state;
        localStorage.setItem('user', user);
        console.log(user);

      };
    render() {
        return (
            <div className="text-center" >
                <button name="Student" value={this.state.user} onSubmit={this.handleSubmit}>Student</button>
                <button name="Teacher" value={this.state.user} onSubmit={this.handleSubmit}>Teacher</button>
            </div>
        )
    }
}

export default start
