import React, { Component } from "react";
import axios from "axios";
import { Link, Redirect } from "react-router-dom";
import moment from 'moment';
import  Modal from 'react-bootstrap/Modal'
import Button from 'react-bootstrap/Button'

export class Subscription extends Component {

    state = {
       courses:[],
        subscription:"/mysubscription",
        myprofile: "/userProfile",
        myposts:"/myposts",
        likedposts: "/likedposts",
        modalShow:false,
        showId:0
    }
    handleClose=()=>{
        this.setState({modalShow:false});
    }

    handleShow=(e,id)=>{
        this.setState({modalShow:true});
        this.setState({showId:id});

    }

    componentDidMount() {
      if(!localStorage.getItem("isLoggedIn")){
        {
          this.props.history.push("/");  
        }
    }
        const headers = {
            "Content-Type": "application/json",
            Accept: "application/json",
            Authorization: `JWT ${localStorage.getItem("token")}`

          };
          const url = "http://localhost:8000/api/courses?branch=&institute=";
          axios
            .get(url, { headers })
            .then((result) => {


              this.setState({ courses: result.data });


              console.log(result.data);
            })
            .catch((error) => {
              console.log(error);

            });
      }

      courseHandler = (e, id,name) => {

        console.log(id);
        console.log(name);
        localStorage.setItem("course",id);
            const url = "/posts/" + localStorage.getItem('course');
            this.props.history.push(url);


          }

    subscribeHandler=(e,id,subs)=>{


            if(!subs){

              const url="http://localhost:8000/api/email-notification-subscription";
              const headers = {
                "Content-Type": "application/json",
                Accept: "application/json",
                Authorization: `JWT ${localStorage.getItem("token")}`

              };
              const user = localStorage.getItem('userid');


            const course=id;

            let bodyFormData = new FormData();
            bodyFormData.set("course",course);
            bodyFormData.set("user", user);

            axios
            .post(url, bodyFormData, { headers })
            .then((result) => {


                // this.setState({ profile: result.data });


                console.log(result.data);
            })
            .catch((error) => {
                console.log(error);

            });

            }

            else{

              const url="http://localhost:8000/api/email-notification-subscription/"+subs;
              const headers = {
                "Content-Type": "application/json",
                Accept: "application/json",
                Authorization: `JWT ${localStorage.getItem("token")}`

              };

            axios
            .delete(url,  { headers })
            .then((result) => {


                // this.setState({ profile: result.data });


                console.log(result.data);
            })
            .catch((error) => {
                console.log(error);

            });

            }
            this.handleClose();
            window.setTimeout(function(){window.location.reload()},200)
          }
    render() {
        return (
            <div>
                <div className="container">
                <div>

<a type="button" href={this.state.myprofile}><button style={{ marginRight: '4px'}} className="btn btn-outline-success ">My Profile</button></a>
<a type="button" href={this.state.myposts}><button style={{ marginRight: '4px' }} className="btn btn-outline-success ">My Posts</button></a>
<a type="button" href={this.state.likedposts}><button style={{ marginRight: '4px' }} className="btn btn-outline-success ">Liked Posts</button> </a>
<a type="button" href={this.state.subscription}><button style={{ marginRight: '4px',color: 'white', backgroundColor: "green"  }} className="btn btn-outline-success ">My Subscription</button> </a>
</div>
            <br></br>

            <div>
            {this.state.courses.length == 0 ? <div><h1>No Courses added</h1></div> :
          this.state.courses.map((val) => {
            return (
              <div className=" container ">
                  {val.email_notification_subscription?
                <div className="btn-group ">

                  <button style={{width: "90%", textAlign:"center"}}  className="btn btn-outline-success btn-block crs" onClick={(e) => this.courseHandler(e, val.id,val.name)}>
                    <h3>{val.name} By </h3>
                    <h3>{val.branch.name}</h3>


                  </button>
                  <button style={{width: "40px", height: "40px"}} className={val.email_notification_subscription?"btn btn-success btn-block ":"btn btn-secondary btn-block"} variant="primary" onClick={(e)=>this.handleShow(e,val.id)} >
                  <h5 style={{alignItems:"center"}}>{val.email_notification_subscription?<i  class="fas fa-bell-slash"></i>:<i  class="fas fa-bell"></i>}</h5>
      </button>
      {this.state.showId==val.id?
      <Modal show={this.state.modalShow} onHide={this.handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>{val.email_notification_subscription?<h4>Unsubscribe</h4>: <h4>Subscribe</h4>}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
         {val.email_notification_subscription?
          <p>
            Are you sure you want to unsubscribe {val.name}?
          </p>:<p>Are you sure you want to Subscribe {val.name}?</p>
          }
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={this.handleClose}>
            Close
          </Button>
          <Button variant="primary" onClick={(e) => this.subscribeHandler(e, val.id,val.email_notification_subscription)}>
            Yes
          </Button>
        </Modal.Footer>
      </Modal>:<div></div>}
                  {/* <button  style={{width: "40px", height: "40px"}} className={val.email_notification_subscription?"btn btn-success btn-block ":"btn btn-secondary btn-block"} onClick={(e) => this.subscribeHandler(e, val.id,val.email_notification_subscription)}>
                    <h5 style={{alignItems:"center"}}>{val.email_notification_subscription?<i  class="fas fa-bell-slash"></i>:<i  class="fas fa-bell"></i>}</h5>

                  </button> */}

                </div>
                :<div></div>
                  }

              </div>

            );
          })
          }
          </div>
          </div>
            </div>
        )
    }
}

export default Subscription
