import React, { Component } from "react";
import axios from "axios";
import { Link, Redirect } from "react-router-dom";
import moment from 'moment';


export class Likedposts extends Component {

    state = {
        posts: [],

        subscription:"/mysubscription",
        myprofile: "/userProfile",
        myposts:"/myposts",
        likedposts: "/likedposts"
    }
    componentDidMount() {
        const headers = {
          "Content-Type": "application/json",
          Accept: "application/json",
          Authorization: `JWT ${localStorage.getItem("token")}`

        };
        const url = "http://localhost:8000/api/posts?course=&created_by=";
        axios
          .get(url, { headers })
          .then((result) => {


            this.setState({ posts: result.data });


            console.log(result.data);
          })
          .catch((error) => {
            console.log(error);

          });
      }

    render() {
        return (
            <div>
                <div className="container">
                <div>

<a type="button" href={this.state.myprofile}><button style={{ marginRight: '4px' }} className="btn btn-outline-success ">My Profile</button></a>
<a type="button" href={this.state.myposts}><button style={{ marginRight: '4px' }} className="btn btn-outline-success ">My Posts</button></a>
<a type="button" href={this.state.likedposts}><button style={{ marginRight: '4px', color: 'white', backgroundColor: "green" }} className="btn btn-outline-success ">Liked Posts</button> </a>
<a type="button" href={this.state.subscription}><button style={{ marginRight: '4px' }} className="btn btn-outline-success ">My Subscription</button> </a>
</div>
            </div>

            <div>
            {this.state.posts.length == 0 ? <div><h2>No Posts Liked Ywt</h2></div> : this.state.posts.map((val) => {

return (

  <div className=" container ">
                  {val.email_notification_subscription?
                <div className="btn-group ">

                  <button style={{width: "90%", textAlign:"center"}}  className="btn btn-outline-success btn-block" onClick={(e) => this.courseHandler(e, val.id,val.name)}>
                    <h3>{val.name}</h3>
                    <h3>{val.branch}</h3>


                  </button>
                  <button  style={{width: "40px", height: "40px"}} className={val.email_notification_subscription?"btn btn-success btn-block ":"btn btn-secondary btn-block"} onClick={(e) => this.subscribeHandler(e, val.id,val.email_notification_subscription)}>
                    <h5 style={{alignItems:"center"}}>{val.email_notification_subscription?<i  class="fas fa-bell-slash"></i>:<i  class="fas fa-bell"></i>}</h5>

                  </button>

                </div>
                :<div></div>
                  }

              </div>


);
})}
            </div>
            </div>
        )
    }
}

export default Likedposts
