import React, { Component } from "react";
import axios from "axios";
import { Link, Redirect } from "react-router-dom";
import moment from 'moment';

export class Courses extends Component {
  state = {


    courses: [],
  };


  courseHandler = (e, id,name) => {

console.log(id);
console.log(name);
localStorage.setItem("course",id);
    const url = "/posts/" + localStorage.getItem('course');
    this.props.history.push(url);


  }
  componentDidMount() {
    const headers = {
        "Content-Type": "application/json",
        Accept: "application/json",
        Authorization: `JWT ${localStorage.getItem("token")}`

      };
      const url = "http://localhost:8000/api/courses?branch="+this.props.match.params.id+"&institute="+ localStorage.getItem('institute');
      axios
        .get(url, { headers })
        .then((result) => {


          this.setState({ courses: result.data });


          console.log(result.data);
        })
        .catch((error) => {
          console.log(error);

        });
  }




  render() {
    //console.log(this.props);
    return (
      <div>

        <div className="container">
          {this.state.courses.length == 0 ? <div><h1>No Courses added</h1></div> :
          this.state.courses.map((val) => {
            return (
              <div className=" container ">
                <div className="batches">
                  <button style={{width: '50%'}} className="btn btn-outline-success btn-block" onClick={(e) => this.courseHandler(e, val.id,val.name)}>
                    <h3>{val.name}</h3>


                  </button>
                  <button style={{width: '10%', }} className="btn btn-outline-success btn-block" onClick={(e) => this.courseHandler(e, val.id,val.name)}>
                    <h3>Bell</h3>


                  </button>

                </div>
                <br></br>
              </div>

            );
          })
          }
        </div>
      </div>
    );
  }
}

export default Courses;
