import React, { Component } from "react";
import axios from "axios";
import { Link, Redirect } from "react-router-dom";
import moment from 'moment';


export class Myposts extends Component {

    state = {
        profile: [],
        isEditing: false,
        full_name: "",
        phone: "",
        gender: "",
        degree: "",
        subscription:"/mysubscription",
        myprofile: "/userProfile",
        myposts:"/myposts",
        likedposts: "/likedposts"
    }

    componentDidMount() {
        const headers = {
            "Content-Type": "application/json",
            Accept: "application/json",
            Authorization: `JWT ${localStorage.getItem("token")}`

          };
          const url = "http://localhost:8000/api/posts?course=&created_by="+localStorage.getItem("userid");
          axios
            .get(url, { headers })
            .then((result) => {


            //   this.setState({ posts: result.data });


              console.log(result.data);
            })
            .catch((error) => {
              console.log(error);

            });
    }
    render() {
        return (
            <div>
                <div>

<a type="button" href={this.state.myprofile}><button style={{ marginRight: '4px'}} className="btn btn-outline-success ">My Profile</button></a>
<a type="button" href={this.state.myposts}><button style={{ marginRight: '4px' , color: 'white', backgroundColor: "green" }} className="btn btn-outline-success ">My Posts</button></a>
<a type="button" href={this.state.likedposts}><button style={{ marginRight: '4px' }} className="btn btn-outline-success ">Liked Posts</button> </a>
<a type="button" href={this.state.subscription}><button style={{ marginRight: '4px' }} className="btn btn-outline-success ">My Subscription</button> </a>
</div>
            </div>
        )
    }
}

export default Myposts
