import React, { Component } from "react";
import axios from "axios";
import { Link, Redirect } from "react-router-dom";
import moment from 'moment';

export class AllBatches extends Component {
  state = {
    branch:"",

    branches: [],
  };


  instituteHandler = (e, id,name) => {

console.log(id);
console.log(name);
localStorage.setItem("branchSelected",id);
    const url = "/courses/" + localStorage.getItem('branchSelected');
    this.props.history.push(url);


  }
  componentDidMount() {
    if(!localStorage.getItem("isLoggedIn")){
      {
        this.props.history.push("/");  
      }
  }
    const headers = {
        "Content-Type": "application/json",
        Accept: "application/json"

      };
      const url = "http://localhost:8000/api/branches?institute="+localStorage.getItem('institute');
      axios
        .get(url, { headers })
        .then((result) => {


          this.setState({ branches: result.data });


          console.log(result.data);
        })
        .catch((error) => {
          console.log(error);

        });
  }




  render() {
    //console.log(this.props);
    return (
      <div>

        <div className="container">

          {this.state.branches.map((val) => {
            return (
              <div className=" container branches">
                 <button className="btn btn-success " style={{color:'black',paddingTop:'20px'}} onClick={(e) => this.instituteHandler(e, val.id,val.name)}>
                <div className="batches">

                    <h3>{val.name}</h3>



                </div>
                </button>
                <br></br>
              </div>

            );
          })}
        </div>
      </div>
    );
  }
}

export default AllBatches;
