import React, { Component } from "react";
import axios from "axios";
import { Link, Redirect } from "react-router-dom";
import moment from 'moment';

export class Subscription extends Component {

    state = {
       courses:[],
        subscription:"/mysubscription",
        myprofile: "/userProfile",
        myposts:"/myposts",
        likedposts: "/likedposts"
    }

    componentDidMount() {
        const headers = {
            "Content-Type": "application/json",
            Accept: "application/json",
            Authorization: `JWT ${localStorage.getItem("token")}`

          };
          const url = "http://localhost:8000/api/courses?branch=&institute=";
          axios
            .get(url, { headers })
            .then((result) => {


              this.setState({ courses: result.data });


              console.log(result.data);
            })
            .catch((error) => {
              console.log(error);

            });
      }
    render() {
        return (
            <div>
                <div className="container">
                <div>

<a type="button" href={this.state.myprofile}><button style={{ marginRight: '4px'}} className="btn btn-outline-success ">My Profile</button></a>
<a type="button" href={this.state.myposts}><button style={{ marginRight: '4px' }} className="btn btn-outline-success ">My Posts</button></a>
<a type="button" href={this.state.likedposts}><button style={{ marginRight: '4px' }} className="btn btn-outline-success ">Liked Posts</button> </a>
<a type="button" href={this.state.subscription}><button style={{ marginRight: '4px',color: 'white', backgroundColor: "green"  }} className="btn btn-outline-success ">My Subscription</button> </a>
</div>
            </div>

            <div>
            {this.state.courses.length == 0 ? <div><h1>No Courses added</h1></div> :
          this.state.courses.map((val) => {
            return (
              <div className=" container ">
                <div className="btn-group ">

                  <button style={{width: "90%", textAlign:"center"}}  className="btn btn-outline-success btn-block" onClick={(e) => this.courseHandler(e, val.id,val.name)}>
                    <h3>{val.name}</h3>


                  </button>
                  <button  style={{width: "40px", height: "40px"}} className={val.email_notification_subscription?"btn btn-success btn-block ":"btn btn-secondary btn-block"} onClick={(e) => this.subscribeHandler(e, val.id,val.email_notification_subscription)}>
                    <h5 style={{alignItems:"center"}}>{val.email_notification_subscription?<i  class="fas fa-bell-slash"></i>:<i  class="fas fa-bell"></i>}</h5>



                  </button>

                </div>
                <br></br>
              </div>

            );
          })
          }
          </div>
            </div>
        )
    }
}

export default Subscription
