import React, { Component } from "react";
import axios from "axios";
import { Link, Redirect } from "react-router-dom";
import moment from 'moment';

export class TeacherHome extends Component {
  state = {

    created_by: localStorage.getItem("userid"),
    role: localStorage.getItem("role"),
    data: [],
  };

  batchhandler=(e,id)=>{
    const site="/allthings"
    console.log(site);
    localStorage.setItem("isbatch",id);
    this.props.history.push(site);

  }
  componentDidMount() {

    const headers = {
      "Content-Type": "application/json",
      Accept: "application/json",
      Authorization: `JWT ${localStorage.getItem("token")}`,
    };
    // Typical usage (don't forget to compare props):
    console.log(this.props);
    const url =
      "http://localhost:8000/api/batch?created_by=" + this.state.created_by;
    console.log(url);

    axios
      .get(url, { headers })
      .then((result) => {
        console.log(result.data);
        this.setState({ data: result.data });
      })
      .catch((error) => {
        console.log(error);
      });
  };

  handleadd=(event) =>{
    this.props.history.push("/batch");
  }


  render() {
    //console.log(this.props);
    return (
      <div>

        <div className="container">
          <div className="card card-login mx-auto mt-5">



                <div >
                  <button onclick={this.handleadd} className="btn btn-primary btn-block" type="submit">
                    Add batch
                  </button>
                </div>








          </div>
          {this.state.data.map((val) => {
                  return (
                    <button onClick={(e) => this.batchhandler(e,val.id)}>
                    <div className=" container cardly">
                       <h3>Batch Name:{val.name}</h3>
                      <h3>Created At: {moment(val.created_at).format("dddd, MMMM Do YYYY, h:mm:ss a")}</h3>
                      <h3>Modified At:{moment(val.modified_at).format("dddd, MMMM Do YYYY, h:mm:ss a")}</h3>
                      <h3>Meet Link: {val.meet_link}</h3>
                    </div>
                    </button>
                  );
                })}
        </div>
      </div>
    );
  }
}

export default TeacherHome;
