import React, { Component } from "react";
import axios from "axios";
import { Link, Redirect } from "react-router-dom";
export class AllBatches extends Component {

  state = {
    name: "",
    meet: "http://google.com" ,
    created_by:  localStorage.getItem("userid"),
    role: localStorage.getItem("user")
  };
  handlehandleNameChange = (event) => {
    this.setState({ name: event.target.value });
  };
  handlehandleMeetChange = (event) => {
    this.setState({ meet: event.target.value });
  };



  handleGet = (event) => {
    event.preventDefault();
    const headers = {
      "Content-Type": "application/json",
      Accept: "application/json",
      Authorization: `JWT ${localStorage.getItem("token")}`,
    };
    // Typical usage (don't forget to compare props):
    console.log(this.props);
    const url = "http://localhost:8000/api/batch" + this.props.location.search;
    console.log(url);

    axios
      .get(url, { headers })
      .then((result) => {
        console.log(result.data);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  handlePost = (event) => {
    console.log(localStorage.getItem("role"));
    console.log(localStorage.getItem("userid"));
    event.preventDefault();
    const headers = {
      "Content-Type": "application/json",
      Accept: "application/json",
      Authorization: `JWT ${localStorage.getItem("token")}`,
    };
    // Typical usage (don't forget to compare props):
    console.log(this.props);

    const url = "http://localhost:8000/api/batch" + this.props.location.search;
    console.log(url);
    const name=this.state.name;
    const meet=this.state.meet;
    const created_by=this.state.created_by;
    let bodyFormData = new FormData();

    bodyFormData.set("name", name);
    bodyFormData.set("meet_link", meet);
    bodyFormData.set("created_by", created_by);
    console.log(bodyFormData);
    axios
      .post(url,bodyFormData, { headers })
      .then((result) => {
        console.log(result.data);
      })
      .catch((error) => {
        console.log(error);
      });
  };


  render() {
    //console.log(this.props);
    return (
      <div>
        <h1>Hello {this.props.match.params.id}</h1>
        <div className="container">
          <div className="card card-login mx-auto mt-5">
            <div className="card-header">Create Batch</div>
            <div className="card-body">
              <form onSubmit={this.handlePost}>
                <div className="form-group">
                  <div className="form-label-group">
                    <input
                      type="text"
                      id="name"
                      className="form-control"
                      placeholder="Name"
                      name="name"
                      onChange={this.handleNameChange}
                      required
                    />
                    <label htmlFor="name">Name</label>
                  </div>
                </div>
                <div className="form-group">
                  <div className="form-label-group">
                    <input
                      type="url"
                      id="meet"
                      className="form-control"
                      placeholder="Meet Link"
                      name="meet"
                      onChange={this.handleMeetChange}
                      required
                    />
                    <label htmlFor="meet">Meet Link</label>
                  </div>
                </div>



                <div className="form-group">
                  <button className="btn btn-primary btn-block" type="submit">
                    Add batch
                  </button>
                </div>
              </form>
              <div className="form-group">
                  <button className="btn btn-primary btn-block" onClick={this.handleGet}>
                    Show Batches
                  </button>
                </div>

            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default AllBatches;
