import React, { Component } from "react";
import {Link, Redirect} from 'react-router-dom';
export class start extends Component {
  state = {
    user: "",
  };

  handleSubmitStudent = () => {
    localStorage.setItem("user", "student");
    this.setState({ user: "student" });
    const { user } = this.state;


  };
  handleSubmitTeacher = () => {
    localStorage.setItem("user", "teacher");
    this.setState({ user: "teacher" });
    const { user } = this.state;


  };
  renderRedirect = () => {
    if(this.state.user=="student" || this.state.user=="teacher") 
        return <Redirect to='/login'/>

    };
  render() {
    return (
      <div className="container">
        <div className="text-center">
          <button onClick={this.handleSubmitStudent}>Student </button>
          <button onClick={this.handleSubmitTeacher}>Teacher</button>
        </div>
        {this.renderRedirect()}
      </div>
    );
  }
}

export default start;
