import React, { Component } from "react";
import axios from "axios";
import { Link, Redirect } from "react-router-dom";
import moment from 'moment';


export class Myposts extends Component {

    state = {
        posts: [],
        subscription: "/mysubscription",
        myprofile: "/userProfile",
        myposts: "/myposts",
        likedposts: "/likedposts",
        editid:0,
        editable: false,
        createLink: '',
    createDescription: ''
    }

    handleCreateDescriptionChange = event => {
        this.setState({ createDescription: event.target.value })
      }

      handleCreateLinkChange = event => {
        this.setState({ createLink: event.target.value })
      }

    componentDidMount() {
        const headers = {
            "Content-Type": "application/json",
            Accept: "application/json",
            Authorization: `JWT ${localStorage.getItem("token")}`

        };
        const url = "http://localhost:8000/api/posts?course=&created_by=" + localStorage.getItem("userid");
        axios
            .get(url, { headers })
            .then((result) => {


                this.setState({ posts: result.data });


                console.log(result.data);
            })
            .catch((error) => {
                console.log(error);

            });
    }

    deleteHandler=(e,id)=>{
        const headers = {
            "Content-Type": "application/json",
            Accept: "application/json",
            Authorization: `JWT ${localStorage.getItem("token")}`

        };
        const url = "http://localhost:8000/api/posts/" + id;
        axios
            .delete(url, { headers })
            .then((result) => {


                // this.setState({ posts: result.data });


                console.log(result.data);
            })
            .catch((error) => {
                console.log(error);

            });
        window.location.reload();
    }

    editHandler=(e,id)=>{
        this.setState({ editid: id });
        this.setState({editable:true});
    }
    render() {
        return (
            <div>
                <div className="container">
                    <div>

                        <a type="button" href={this.state.myprofile}><button style={{ marginRight: '4px' }} className="btn btn-outline-success ">My Profile</button></a>
                        <a type="button" href={this.state.myposts}><button style={{ marginRight: '4px', color: 'white', backgroundColor: "green" }} className="btn btn-outline-success ">My Posts</button></a>
                        <a type="button" href={this.state.likedposts}><button style={{ marginRight: '4px' }} className="btn btn-outline-success ">Liked Posts</button> </a>
                        <a type="button" href={this.state.subscription}><button style={{ marginRight: '4px' }} className="btn btn-outline-success ">My Subscription</button> </a>
                    </div>
                    <br></br>

                    {this.state.posts.length == 0 ? <div><h2>No Posts Yet</h2></div> : this.state.posts.map((val) => {

                        return (
                            <div className=" container ">
                                {this.state.editable && this.state.editid==val.id?
                                 <div>
                                 <form onSubmit={this.handleSubmit}>
                                   <div className='form-group'>
                                     <div className='form-label-group'>
                                       <input
                                         className={'form-control '}
                                         id='link'
                                         placeholder='Link'
                                         type='text'
                                         name='link'
                                         onChange={this.handleCreateLinkChange}
                                         autoFocus
                                         required
                                       />
                                       <label htmlFor='link'>Link</label>
                                     </div>

                                     <div className='form-label-group'>
                                       <input
                                         className={'form-control '}
                                         id='description'
                                         placeholder='Description'
                                         type='text'
                                         name='description'
                                         onChange={this.handleCreateDescriptionChange}
                                         autoFocus
                                         required
                                       />
                                       <label htmlFor='description'>Description</label>
                                     </div>
                                   </div>

                                   <div className='form-group'>
                                     <button className='btn btn-primary btn-block' type='submit'>
                                       Post &nbsp;&nbsp;&nbsp;
                                     </button>
                                   </div>
                                 </form>
                               </div>
                                :<div className="posts">


                                    <h6 style={{ textAlign: 'left' }}> Posted on : {moment(val.created_at).format("dddd, MMMM Do YYYY, h:mm:ss a")} </h6>
                                    <h3>{val.description}</h3>

                                    <a target="_blank" href={val.link}><h3> {val.link}</h3></a>


                                    <h3>Upvote Count- {val.upvote_count}</h3>
                                    <button className="btn btn-danger"  onClick={(e) => this.deleteHandler(e, val.id)}>Delete</button>

                                    <button className="btn btn-primary"  onClick={(e) => this.editHandler(e, val.id)}> Edit Post</button>




                                </div>
                    }
                    <br></br>
                            </div>


                        );
                    })}
                    </div>
                </div>
                )
    }
}

                export default Myposts
