import React, { Component } from "react";
import axios from "axios";
import { Link, Redirect } from "react-router-dom";
import moment from 'moment';

export class userProfile extends Component {


    state = {
        profile: [],
        isEditing: false,
        full_name:"",
        phone:"",
        gender:"",
        degree:""
    }

    handleDegree=(event)=>{
        this.setState({degree:event.target.value});
    }
    handleFullname=(event)=>{
        this.setState({full_name:event.target.value});
    }
    handlePhone=(event)=>{
        this.setState({phone:event.target.value});
    }
    handleGender=(event)=>{
        this.setState({gender:event.target.value});
    }
    editHandler = (event) => {
        this.setState({ isEditing: true });
    }
    componentDidMount() {
        const headers = {
            "Content-Type": "application/json",
            Accept: "application/json",
            Authorization: `JWT ${localStorage.getItem("token")}`

        };


        const url = "http://localhost:8000/api/user-profile";
        axios
            .get(url, { headers })
            .then((result) => {


                this.setState({ profile: result.data });
                this.setState({full_name:result.data.full_name});
                this.setState({phone:result.data.phone});
                this.setState({degree:result.data.degree});
                this.setState({gender:result.data.gender});
                console.log(result.data);
            })
            .catch((error) => {
                console.log(error);

            });

    }

    handleSubmit=(event)=>{
        event.preventDefault();
        console.log(this.state.phone);
        console.log(this.state.full_name);
        console.log(this.state.gender);
        console.log(this.state.degree);


        const degree = this.state.degree;
    const gender = this.state.gender;
    const full_name = this.state.full_name;
    const phone=this.state.phone;

    let bodyFormData = new FormData();
    bodyFormData.set("gender",gender);
    bodyFormData.set("degree", degree);
    bodyFormData.set("full_name", full_name);
    bodyFormData.set("phone", phone);


        const headers = {
            "Content-Type": "application/json",
            Accept: "application/json",
            Authorization: `JWT ${localStorage.getItem("token")}`

        };
        const url = "http://localhost:8000/api/user-profile";
        axios
            .put(url, bodyFormData, { headers })
            .then((result) => {


                // this.setState({ profile: result.data });


                console.log(result.data);
            })
            .catch((error) => {
                console.log(error);

            });
        // this.setState({isEditing:false});
        // window.location.reload();
    }
    render() {
        return (
            <div>
                <div className="container">

                    {!this.state.isEditing ?

                        <div className=" container " >
                            <button className="btn btn-outline-success " onClick={this.editHandler}>Edit Details</button>
                            <div className="profile">

                                <ul>
                                    <h4 > Full Name:- {this.state.profile.full_name} </h4>
                                    <h4>Email:- {this.state.profile.email} </h4>
                                    <h4>Gender:- {this.state.profile.gender} </h4>
                                    <h4>Institute:- {this.state.profile.institute}</h4>
                                    <h4>Branch:- {this.state.profile.branch}</h4>
                                    <h4>Degree:- {this.state.profile.degree} </h4>
                                    <h4>Contact Number:- {this.state.profile.phone} </h4>
                                </ul>
                            </div>
                            <br></br>
                        </div>


                        : <div>
                            <form onSubmit={this.handleSubmit}>
                                <div className="form-group">
                                    <div className="form-group">
                                    <label htmlFor="full_name">Full Name</label>
                                        <input
                                            className={
                                                "form-control "
                                            }
                                            id="full_name"
                                            placeholder="Full Name"
                                            type="text"
                                            name="full_name"
                                            onChange={this.handleFullname}
                                            autoFocus
                                            required
                                            value={this.state.full_name}
                                        />


                                    </div>

                                    <div className="form-group">
                                    <label for="phone">Contact Number</label>
                                        <input
                                            className={
                                                "form-control "
                                            }
                                            id="phone"
                                            placeholder="Phone"
                                            type="text"
                                            name="phone"
                                            onChange={this.handlePhone}
                                            autoFocus
                                            required
                                            value={this.state.phone}
                                        />


                                    </div>
                                    <div class="form-group">
                                        <label for="gender">Gender</label>
                                        <select reequired class="custom-select" id="gender" name="gender" onChange={this.handleGender} value={this.state.gender} >
                                            <option selected  >Choose...</option>
                                            <option value={"male"} >Male</option>
                                            <option value={"female"} >Female</option>
                                            <option value={"other"} >Other</option>

                                        </select>
                                    </div>

                                    <div class="form-group">
                                        <label for="degree">Degree</label>
                                        <select reequired class="custom-select" id="degree" name="degree" onChange={this.handleDegree} value={this.state.degree}>
                                            <option selected value={0} >Choose...</option>
                                            <option value={"btech"} >BTech</option>
                                            <option value={"mtech"} >MTech</option>
                                            <option value={"phd"} >Phd</option>
                                            <option value={"msc"} >Msc</option>
                                            <option value={"mba"} >MBA</option>
                                        </select>
                                    </div>
                                </div>

                                <div className="form-group">
                                    <button
                                        className="btn btn-primary btn-block"
                                        type="submit"

                                    >
                                        Change Details &nbsp;&nbsp;&nbsp;

                                    </button>
                                </div>
                            </form></div>}
                </div>
            </div>
        )
    }
}

export default userProfile
