import React, { Component } from "react";
import axios from "axios";
import { Link, Redirect } from "react-router-dom";
import moment from 'moment';

export class userProfile extends Component {


    state = {
        profile: [],
        isEditing: false
    }

    editHandler = (event) => {
        this.setState({ isEditing: true });
    }
    componentDidMount() {
        const headers = {
            "Content-Type": "application/json",
            Accept: "application/json",
            Authorization: `JWT ${localStorage.getItem("token")}`

        };
        const url = "http://localhost:8000/api/user-profile";
        axios
            .get(url, { headers })
            .then((result) => {


                this.setState({ profile: result.data });


                console.log(result.data);
            })
            .catch((error) => {
                console.log(error);

            });
    }
    render() {
        return (
            <div>
                <div className="container">
                    <button className="btn btn-outline-success " onClick={this.editHandler}>Edit Details</button>
                    {!this.state.isEditing ? this.state.profile.map((val) => {

                        return (
                            <div className=" container ">
                                <div className="profile">

                                    <h4>Email:- {val.email} </h4>
                                    <h4 > Full Name:- {val.full_name} </h4>
                                    <h3>{val.description}</h3>

                                </div>
                                <br></br>
                            </div>

                        );
                    }):<div></div>}
                </div>
            </div>
        )
    }
}

export default userProfile
